mapper.select:
SELECT COUNT(*)
FROM member

WHERE email = #{value}


WHERE nick_name = #{value}

mapper.update:
UPDATE member
SET password = #{password}, nick_name = #{nickName}, phone_number = #{phoneNumber}, birth = #{birth}
, address = #{address}, name = #{name}, gender = #{gender}
WHERE email = #{email}

UPDATE member
SET auth = #{auth}
WHERE email = #{email}

mapper.select:
SELECT email, session_id, logout_time
FROM member
WHERE session_id = #{sessionId}
