mapper.select:
SELECT ROUND(AVG(star_rate), 2)
FROM review_board
WHERE business_no=#{businessNo}

SELECT COUNT(star_rate)
FROM review_board
WHERE business_no=#{businessNo}

SELECT review_bno, email, title, content, last_updated, star_rate, like_cnt
FROM review_board
WHERE review_bno = #{reviewBno}

SELECT review_bno, file_path
FROM review_upload
WHERE review_bno = #{reviewBno}

mapper.update:
UPDATE review_board
SET like_cnt = (SELECT like_cnt FROM review_board WHERE review_bno = #{reviewBno}) + 1
WHERE review_bno = #{reviewBno}

UPDATE review_board
SET like_cnt = (SELECT like_cnt FROM review_board WHERE review_bno = #{reviewBno}) - 1
WHERE review_bno = #{reviewBno}

mapper.select:
SELECT like_cnt
FROM review_board
WHERE review_bno = #{reviewBno}
